Joomla.submitform=function(task){let adminForm=document.getElementById('adminForm');if(document.formvalidator.isValid(adminForm)){let message=null;switch(adminForm.delete.value){case'all':message='COM_OSCONTENT_DELETE_ALL';break;case'content':message='COM_OSCONTENT_DELETE_CONTENT';break;default:alert(Joomla.JText._('COM_OSCONTENT_DELETE_FAIL'));return;}
if(confirm(Joomla.JText._(message))){adminForm.task.value=task;adminForm.submit();}}}